contracts/staking/StakingController.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import {
  OwnableUpgradeable
} from '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import { Initializable } from '@openzeppelin/contracts/proxy/Initializable.sol';
import { StakingControllerLib } from './StakingControllerLib.sol';
import { ConnectToken as CNFI } from '../token/CNFI.sol';
import { sCNFI } from '../token/sCNFI.sol';
import { pCNFIFactoryLib } from '../token/lib/pCNFIFactoryLib.sol';
import {
  SafeMathUpgradeable
} from '@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol';
import {
  MathUpgradeable as Math
} from '@openzeppelin/contracts-upgradeable/math/MathUpgradeable.sol';
import { pCNFI } from '../token/pCNFI.sol';
import { Create2 } from '@openzeppelin/contracts/utils/Create2.sol';
import { FactoryLib } from '../lib/FactoryLib.sol';
import { ICNFI } from '../interfaces/ICNFI.sol';
import { CNFITreasury } from '../treasury/CNFITreasury.sol';
import { ViewExecutor } from '../util/ViewExecutor.sol';
import { StakingControllerTemplate } from './StakingControllerTemplate.sol';
import { UpdateToLastLib } from './UpdateToLastLib.sol';
import { LibNote } from '../lib/dss-lib.sol';

contract StakingController is StakingControllerTemplate, ViewExecutor, LibNote {
  event Redeemed(address indexed user, uint256 amountToRedeem, uint256 bonuses);
  event Staked(
    address indexed user,
    uint256 amount,
    uint256 indexed commitmentTier,
    uint256 minimum,
    bool timeLocked
  );
  event Unstaked(address indexed user, uint256 amount, uint256 slashed);

  using SafeMathUpgradeable for *;

  function initialize(
    address _cnfi,
    address _sCnfi,
    address _cnfiTreasury
  ) public initializer {
    __Ownable_init_unchained();
    cnfi = ICNFI(_cnfi);
    sCnfi = sCNFI(_sCnfi);
    pCnfiImplementation = Create2.deploy(
      0,
      pCNFIFactoryLib.getSalt(),
      pCNFIFactoryLib.getBytecode()
    );
    cnfiTreasury = CNFITreasury(_cnfiTreasury);
    rewardInterval = 1 days;
    cycleInterval = 180 days;
  }

  function govern(
    uint256 _cycleInterval,
    uint256 _rewardInterval,
    uint256 _inflateBy,
    uint256 _inflatepcnfiBy,
    uint256 _baseUnstakePenalty,
    uint256 _commitmentViolationPenalty,
    uint256[] memory _multipliers,
    uint256[] memory _cycles,
    uint256[] memory _minimums
  ) public onlyOwner {
    if (_baseUnstakePenalty > 0) baseUnstakePenalty = _baseUnstakePenalty;
    if (_commitmentViolationPenalty > 0)
      commitmentViolationPenalty = _commitmentViolationPenalty;
    if (_cycleInterval > 0) {
      cycleInterval = _cycleInterval;
      nextCycleTime = block.timestamp + cycleInterval;
    }
    if (_rewardInterval > 0) {
      rewardInterval = _rewardInterval;
      nextTimestamp = block.timestamp + rewardInterval;
    }
    if (_inflateBy > 0) {
      if (_inflateBy != inflateBy) {
        inflateByChanged.push(currentDay);
        inflateByValues[currentDay] = StakingControllerLib.InflateByChanged(
          totalWeight,
          inflateBy
        );
      }
      inflateBy = _inflateBy;
    }
    if (_inflatepcnfiBy > 0) inflatepcnfiBy = _inflatepcnfiBy;
    tiersLength = _multipliers.length;
    for (uint256 i = 1; i <= tiersLength; i++) {
      tiers[i] = StakingControllerLib.Tier(
        _multipliers[i - 1],
        _minimums[i - 1],
        _cycles[i - 1]
      );
    }
  }

  function fillFirstCycle() public onlyOwner {
    triggerCycle(true);
  }

  function triggerCycle(bool force) internal {
    if (force || block.timestamp > nextCycleTime) {
      nextCycleTime = block.timestamp + cycleInterval;
      uint256 _currentCycle = ++currentCycle;
      cycles[_currentCycle].pCnfiToken = FactoryLib.create2Clone(
        pCnfiImplementation,
        uint256(
          keccak256(abi.encodePacked(pCNFIFactoryLib.getSalt(), _currentCycle))
        )
      );
      nextTimestamp = block.timestamp + rewardInterval;
      pCnfi = pCNFI(cycles[_currentCycle].pCnfiToken);
      pCnfi.initialize(_currentCycle);
      cycles[_currentCycle].day = 1;
      if (_currentCycle != 1) {
        cycles[_currentCycle].totalWeight = cycles[_currentCycle - 1]
          .totalRawWeight;
        cycles[_currentCycle].totalRawWeight = cycles[_currentCycle - 1]
          .totalRawWeight;
      }
    }
  }

  function determineMultiplier(address user, bool penaltyChange)
    internal
    returns (uint256)
  {
    uint256 currentBalance = sCnfi.balanceOf(user);
    (uint256 multiplier, uint256 amountToBurn) =
      UpdateToLastLib.determineMultiplier(
        cycles,
        currentCycle,
        tiers,
        tiersLength,
        bonusesAccrued,
        lockCommitments,
        penaltyChange,
        user,
        currentBalance
      );
    if (amountToBurn > 0) sCnfi.burn(user, amountToBurn);
    return multiplier;
  }

  function updateToLast(address user) internal {
    UpdateToLastLib.updateToLast(
      currentCycle,
      inflateBy,
      bonusesAccrued,
      cycles,
      user
    );
  }

  function updateWeightsWithMultiplier(address user)
    internal
    returns (uint256)
  {
    uint256 multiplier = determineMultiplier(user, false);
    return
      UpdateToLastLib.updateWeightsWithMultiplier(
        user,
        currentCycle,
        multiplier,
        cycles
      );
  }

  function receiveCallback(address sender, address receiver) public {
    trackDailyRewards(false);
    triggerCycle(false);
    updateToLast(sender);
    updateWeightsWithMultiplier(sender);
    updateDailyStatsToLast(sender, 0, false, false);
    if (receiver != address(0x0)) {
      updateToLast(receiver);
      updateDailyStatsToLast(receiver, 0, false, false);
      updateWeightsWithMultiplier(receiver);
    }
  }

  function determineDailyMultiplier(address sender)
    internal
    returns (uint256 multiplier)
  {
    StakingControllerLib.DailyUser storage user = dailyUsers[sender];
    StakingControllerLib.UserWeightChanges storage weightChange =
      weightChanges[sender];
    multiplier = UpdateToLastLib.determineDailyMultiplier(
      sender,
      user,
      weightChange,
      tiers,
      dailyBonusesAccrued,
      tiersLength
    );
  }

  function deductRewards(address sender, uint256 weight) internal {
    StakingControllerLib.DailyUser storage user = dailyUsers[sender];
    StakingControllerLib.Tier memory tier;
    if (user.commitment > 0) {
      tier = tiers[user.commitment];
      // console.log(user.cyclesHeld, tier.cycles);
      if (weight < tier.minimum && user.cyclesHeld < tier.cycles) {
        user.commitment = 0;
        uint256 amountToBurn =
          dailyBonusesAccrued[sender].mul(commitmentViolationPenalty).div(
            1 ether
          );

        dailyBonusesAccrued[sender] = 0;

        sCnfi.burn(sender, amountToBurn);
      }
    }
  }

  function updateDailyStatsToLast(
    address sender,
    uint256 weight,
    bool penalize,
    bool init
  ) internal {
    StakingControllerLib.DailyUser storage user = dailyUsers[sender];
    StakingControllerLib.UserWeightChanges storage weightChange =
      weightChanges[sender];
    uint256 cycleChange = user.cyclesHeld;
    (user.cycleEnd, user.cyclesHeld) = computeCyclesHeld(
      user.cycleEnd,
      cycleInterval,
      user.cyclesHeld,
      block.timestamp
    );
    if (user.cyclesHeld > 0 && user.cyclesHeld > cycleChange) {
      for (uint256 i = user.cyclesHeld; i > cycleChange; i--) {
        weightChange.changes[i] = user.weight;
      }
      weightChange.totalCyclesSeen = user.cyclesHeld;
    }
    if (penalize || init) {
      weightChange.changes[user.cyclesHeld] = weight;
      user.start = block.timestamp;
    }
    uint256 multiplier = determineDailyMultiplier(sender);
    if (init) user.multiplier = multiplier;

    if (currentDay > 0 && user.lastDaySeen < currentDay) {
      (uint256 redeemable, uint256 bonuses) =
        UpdateToLastLib.updateRedeemable(
          sender,
          inflateBy,
          currentDay,
          user,
          inflateByChanged,
          inflateByValues,
          totalWeight
        );
      dailyBonusesAccrued[sender] = dailyBonusesAccrued[sender].add(bonuses);
      user.redeemable = user.redeemable.add(redeemable);
    }

    if (!init && user.multiplier != multiplier && user.multiplier > 0) {
      uint256 previousUserWeight =
        user.weight.mul(user.multiplier).div(1 ether);
      uint256 newUserWeight = user.weight.mul(multiplier).div(1 ether);

      if (totalWeight == previousUserWeight) totalWeight = newUserWeight;
      else totalWeight = totalWeight.sub(previousUserWeight).add(newUserWeight);
    }
    user.lastTotalWeight = totalWeight;
    user.multiplier = multiplier;
    user.lastDaySeen = currentDay;
    if (penalize) {
      deductRewards(sender, weight);
      user.cycleEnd = block.timestamp + cycleInterval;
      user.cyclesHeld = 0;
      weightChange.totalCyclesSeen = 0;
      user.commitment = 0;
    }
  }

  function recalculateDailyWeights(
    address sender,
    uint256 weight,
    bool penalize
  ) internal {
    StakingControllerLib.DailyUser storage user = dailyUsers[sender];
    uint256 previousMultiplier = user.multiplier;
    if (previousMultiplier == 0) previousMultiplier = 1 ether;
    updateDailyStatsToLast(sender, weight, penalize, true);
    // updating weights
    uint256 prevWeight = user.weight.mul(previousMultiplier).div(1 ether);
    user.weight = weight;
    weight = weight.mul(user.multiplier).div(1 ether);

    totalWeight = totalWeight.add(weight).sub(prevWeight);

    user.lastTotalWeight = totalWeight;
  }

  function recalculateWeights(
    address sender,
    uint256 oldBalance,
    uint256 newBalance,
    bool penalty
  ) internal {
    StakingControllerLib.RecalculateLocals memory locals;
    updateToLast(sender);
    StakingControllerLib.Cycle storage cycle = cycles[currentCycle];
    StakingControllerLib.User storage user = cycle.users[sender];
    user.start = block.timestamp;

    locals.currentWeight = user.currentWeight;
    if (oldBalance != newBalance) {
      if (locals.currentWeight == oldBalance) user.currentWeight = newBalance;
      else
        user.currentWeight = locals.currentWeight.sub(oldBalance).add(
          newBalance
        );
    }
    recalculateDailyWeights(sender, user.currentWeight, penalty);
    locals.previousMultiplier = user.multiplier;
    locals.previousMinimumWeight = user.minimumWeight;
    locals.previousTotalWeight = cycle.totalWeight;
    if (
      user.daysClaimed - cycle.day - 1 > 0 && locals.previousMinimumWeight > 0
    ) {
      locals.totalInflated;
      locals.daysToRedeem;
      if (cycle.day - 1 > user.daysClaimed)
        locals.daysToRedeem = uint256(cycle.day - 1).sub(user.daysClaimed);
      locals.totalInflated = inflateBy.mul(locals.daysToRedeem);
      locals.previousRedeemable = user.redeemable;

      if (locals.totalInflated > 0) {
        locals.amt = locals
          .totalInflated
          .mul(locals.previousMinimumWeight)
          .mul(locals.previousMultiplier)
          .div(1 ether)
          .div(locals.previousTotalWeight);
        user.redeemable = locals.previousRedeemable.add(locals.amt);
        if (locals.previousMultiplier > 1 ether) {
          locals.bonus = locals
            .amt
            .mul(locals.previousMultiplier.sub(1 ether))
            .div(locals.previousMultiplier);
          bonusesAccrued[sender] = bonusesAccrued[sender].add(locals.bonus);
        }
        user.daysClaimed = cycle.day - 1;
      }
    }
    locals.minimumWeight = Math.min(user.minimumWeight, locals.currentWeight);
    locals.multiplier = determineMultiplier(sender, penalty);
    user.minimumWeight = locals.minimumWeight;
    locals.currentTotalWeight = cycle
      .totalWeight
      .add(locals.minimumWeight.mul(locals.multiplier).div(uint256(1 ether)))
      .sub(
      locals.previousMinimumWeight.mul(locals.previousMultiplier).div(
        uint256(1 ether)
      )
    );

    cycle.totalWeight = locals.currentTotalWeight;
    cycle.totalRawWeight = cycle
      .totalRawWeight
      .add(user.currentWeight.mul(locals.multiplier).div(1 ether))
      .sub(locals.currentWeight.mul(locals.previousMultiplier).div(1 ether));

    user.multiplier = locals.multiplier;
  }

  function trackDailyRewards(bool force) internal {
    StakingControllerLib.Cycle storage cycle = cycles[currentCycle];

    if (force || (!cycle.canUnstake && block.timestamp > nextTimestamp)) {
      nextTimestamp = block.timestamp + rewardInterval;
      cycle.reserved = cycle.reserved.add(inflateBy);
      pCnfi.mint(address(cnfiTreasury), inflatepcnfiBy);
      cycle.cnfiRewards[cycle.day] = inflateBy;
      cycle.day++;
      currentDay++;
    }
  }

  function _claim(address user)
    public
    returns (uint256 amountToRedeem, uint256 bonuses)
  {
    (amountToRedeem, bonuses) = calculateRewards(user, 0);
    require(false, string(abi.encode(amountToRedeem, bonuses)));
    // cnfiTreasury.transferToken(address(cnfi), user, amountToRedeem);
  }

  event RewardsClaimed(
    address indexed user,
    uint256 amountToRedeem,
    uint256 bonuses
  );

  function claimRewards()
    public
    returns (uint256 amountToRedeem, uint256 bonuses)
  {
    return claimRewardsWithAmount(0);
  }

  function claimRewardsWithAmount(uint256 amount)
    public
    returns (uint256 amountToRedeem, uint256 bonuses)
  {
    (amountToRedeem, bonuses) = calculateRewards(msg.sender, amount);
    cnfiTreasury.transferToken(address(cnfi), msg.sender, amountToRedeem);
    emit Redeemed(msg.sender, amountToRedeem, bonuses);
  }

  function restakeRewardsWithAmount(uint256 amount, uint256 tier) public note {
    (uint256 amountToRedeem, uint256 bonuses) =
      calculateRewards(msg.sender, amount);
    uint256 oldBalance = sCnfi.balanceOf(msg.sender);

    require(
      (oldBalance + amountToRedeem >= tiers[tier].minimum &&
        tiers[tier].minimum != 0) || tier == 0,
      'must provide more capital to commit to tier'
    );
    if (lockCommitments[msg.sender] <= tier) lockCommitments[msg.sender] = tier;
    bool timeLocked = lockCommitments[msg.sender] > 0;
    uint256 newBalance = oldBalance.add(amountToRedeem);
    sCnfi.mint(msg.sender, amountToRedeem);
    emit Redeemed(msg.sender, amountToRedeem, bonuses);

    for (; tier < tiersLength; tier++) {
      if (tiers[tier].minimum > newBalance) {
        tier--;
        break;
      }
    }

    emit Staked(
      msg.sender,
      amountToRedeem,
      tier,
      tiers[tier].minimum,
      timeLocked
    );
    recalculateWeights(msg.sender, oldBalance, newBalance, true);
  }

  function calculateRewards(address _user, uint256 amt)
    internal
    returns (uint256 amountToRedeem, uint256 bonuses)
  {
    updateDailyStatsToLast(_user, 0, false, false);
    StakingControllerLib.DailyUser storage user = dailyUsers[_user];
    bonuses = dailyBonusesAccrued[_user];
    amountToRedeem = user.redeemable;
    require(amountToRedeem >= amt, 'cannot redeem more than whats available');
    require(amountToRedeem > 0, 'nothing left to redeem');
    if (amt == 0 || amt == amountToRedeem) {
      user.redeemable = 0;
      return (amountToRedeem, bonuses);
    }
    user.redeemable = user.redeemable.sub(amt);
    return (amt, bonuses);
  }

  // function prev_calculateRewards(address _user, uint256 amt)
  //     internal
  //     returns (uint256 amountToRedeem, uint256 bonuses)
  // {
  //     updateToLast(_user);
  //     updateWeightsWithMultiplier(_user);
  //     return
  //         UpdateToLastLib.calculateRewards(
  //             _user,
  //             amt,
  //             cycles,
  //             bonusesAccrued,
  //             inflateBy,
  //             currentCycle
  //         );
  // }

  function stake(uint256 amount, uint256 commitmentTier) public note {
    uint256 oldBalance = sCnfi.balanceOf(msg.sender);
    uint256 newBalance = oldBalance.add(amount);
    cnfi.transferFrom(msg.sender, address(cnfiTreasury), amount);
    sCnfi.mint(msg.sender, amount);
    require(
      (oldBalance + amount >= tiers[commitmentTier].minimum &&
        tiers[commitmentTier].minimum != 0) || commitmentTier == 0,
      'must provide more capital to commit to tier'
    );
    if (commitmentTier >= lockCommitments[msg.sender])
      lockCommitments[msg.sender] = commitmentTier;
    bool isLocked = lockCommitments[msg.sender] > 0;
    StakingControllerLib.DailyUser storage user = dailyUsers[msg.sender];
    if (commitmentTier >= user.commitment) user.commitment = commitmentTier;
    for (; commitmentTier < tiersLength; commitmentTier++) {
      if (tiers[commitmentTier].minimum > newBalance) {
        commitmentTier--;
        break;
      }
    }

    emit Staked(
      msg.sender,
      amount,
      commitmentTier,
      tiers[commitmentTier].minimum,
      isLocked
    );
    recalculateWeights(msg.sender, oldBalance, newBalance, false);
  }

  function unstake(uint256 amount) public note returns (uint256 withdrawable) {
    uint256 oldBalance = sCnfi.balanceOf(msg.sender);
    uint256 penalizedAmount =
      amount.mul(baseUnstakePenalty).div(uint256(1 ether));
    uint256 newBalance;
    if (amount != oldBalance) newBalance = oldBalance.sub(amount);
    else amount = oldBalance;

    uint256 beforeRecalculatedBalance = sCnfi.balanceOf(msg.sender);
    recalculateWeights(msg.sender, oldBalance, newBalance, true);
    uint256 currentBalance = sCnfi.balanceOf(msg.sender);
    uint256 amountLeft = Math.min(currentBalance, amount);

    sCnfi.burn(msg.sender, amountLeft);

    if (penalizedAmount >= currentBalance) {
      withdrawable = 0;
    } else {
      withdrawable = Math.min(
        amountLeft.sub(penalizedAmount),
        currentBalance.sub(penalizedAmount)
      );
    }

    emit Unstaked(
      msg.sender,
      amount,
      beforeRecalculatedBalance -
        Math.min(beforeRecalculatedBalance, currentBalance)
    );
    cnfiTreasury.transferToken(address(cnfi), msg.sender, withdrawable);
  }
}

