// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

import {
    ERC20Upgradeable
} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {
    OwnableUpgradeable
} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {IStakingController} from "../interfaces/IStakingController.sol";

contract ConnectToken is ERC20Upgradeable, OwnableUpgradeable {
    uint256 public unlockAt;
    mapping(address => bool) authorizedBeforeUnlock;
    bytes32 constant STAKING_CONTROLLER_SLOT = keccak256("staking-controller");
    function initialize() public initializer {
      __Ownable_init_unchained();
    }
    function setStakingController(address value) public {
      bytes32 local = STAKING_CONTROLLER_SLOT;
      assembly {
        let slotValue := sload(local)
        if iszero(slotValue) {
          sstore(local, value)
        }
      }
    }
    function getStakingController() public view returns (address returnValue) {
      bytes32 local = STAKING_CONTROLLER_SLOT;
      assembly {
        returnValue := sload(local)
      }
    }   
    function transfer(address target, uint256 amount)
        public
        override
        returns (bool)
    {
        address own = getStakingController();
        IStakingController(own).receiveCallback(msg.sender, target);
        return super.transfer(target, amount);
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address own = getStakingController();
        IStakingController(own).receiveCallback(from, to);
        return super.transferFrom(from, to, amount);
    }

    function approve(address target, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        IStakingController(getStakingController()).receiveCallback(target, msg.sender);
        return super.approve(target, amount);
    }

    function isTest() public pure returns (bool result) {
      uint256 _chainId;
      assembly {
        _chainId := chainid()
      }
      return 31337 == _chainId;
    }
    function mint(address target, uint256 amount) public onlyOwner {
      require(isTest(), "must be test");
      _mint(target, amount);
    }
}

