// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
import { ConnectToken as CNFI } from '../token/CNFI.sol';
import { StakingControllerLib } from './StakingControllerLib.sol';
import { ICNFI } from '../interfaces/ICNFI.sol';
import { pCNFI } from '../token/pCNFI.sol';
import { sCNFI } from '../token/sCNFI.sol';
import { CNFITreasury } from '../treasury/CNFITreasury.sol';
import {
  SafeMathUpgradeable
} from '@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol';
import {
  OwnableUpgradeable
} from '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import { ViewExecutor } from '../util/ViewExecutor.sol';

contract StakingControllerTemplate is OwnableUpgradeable {
  using SafeMathUpgradeable for *;
  uint256 public currentCycle;
  CNFITreasury public cnfiTreasury;
  ICNFI public cnfi;
  sCNFI public sCnfi;
  pCNFI public pCnfi;
  uint256 public nextCycleTime;
  uint256 public cycleInterval;
  uint256 public nextTimestamp;
  uint256 public inflateBy;
  uint256 public inflatepcnfiBy;
  uint256 public rewardInterval;
  uint256 public tiersLength;
  uint256 public baseUnstakePenalty;
  uint256 public commitmentViolationPenalty;
  uint256 public totalWeight;
  mapping(uint256 => StakingControllerLib.Cycle) public cycles;
  mapping(uint256 => StakingControllerLib.Tier) public tiers;
  mapping(address => uint256) public lockCommitments;
  mapping(address => uint256) public bonusesAccrued;
  mapping(address => uint256) public dailyBonusesAccrued;
  mapping(address => StakingControllerLib.UserWeightChanges) weightChanges;
  mapping(address => StakingControllerLib.DailyUser) public dailyUsers;
  uint256[] inflateByChanged;
  mapping(uint256 => StakingControllerLib.InflateByChanged) inflateByValues;
  address public pCnfiImplementation;
  uint256 public currentDay;

  function computeCyclesHeld(
    uint256 cycleEnd,
    uint256 interval,
    uint256 _cyclesHeld,
    uint256 currentTimestamp
  ) public pure returns (uint256, uint256) {
    uint256 cyclesHeld;
    if (cycleEnd == 0) cycleEnd = currentTimestamp + interval;
    else if (currentTimestamp > cycleEnd) {
      uint256 cyclesMissed = currentTimestamp.sub(cycleEnd).div(interval);

      cycleEnd = uint256(1 + cyclesMissed).mul(interval).add(cycleEnd);
      cyclesHeld = cyclesHeld.add(uint256(cyclesMissed));
    }
    cyclesHeld = cyclesHeld.add(_cyclesHeld);
    return (cycleEnd, cyclesHeld);
  }
}

