// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
import { StakingControllerLib } from './StakingControllerLib.sol';
import {
  SafeMathUpgradeable
} from '@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol';
import {
  MathUpgradeable as Math
} from '@openzeppelin/contracts-upgradeable/math/MathUpgradeable.sol';

library UpdateToLastLib {
  using SafeMathUpgradeable for *;
  struct UpdateToLastLocals {
    uint256 cycleNumber;
    uint256 weight;
    uint256 multiplier;
    uint256 lastDaySeen;
    uint256 redeemable;
    uint256 totalWeight;
    uint256 daysToRedeem;
    uint256 bonus;
    uint256 i;
  }

  function updateToLast(
    uint256 currentCycle,
    uint256 inflateBy,
    mapping(address => uint256) storage bonusesAccrued,
    mapping(uint256 => StakingControllerLib.Cycle) storage cycles,
    address user
  ) external {
    UpdateToLastLocals memory locals;
    StakingControllerLib.Cycle storage cycle = cycles[currentCycle];
    if (cycle.users[user].seen) return;
    StakingControllerLib.Cycle storage ptr = cycle;
    locals.cycleNumber = currentCycle;
    while (!ptr.users[user].seen && locals.cycleNumber > 0) {
      ptr = cycles[--locals.cycleNumber];

      if (ptr.users[user].seen) {
        locals.weight = ptr.users[user].currentWeight;
        locals.multiplier = ptr.users[user].multiplier;
        cycle.users[user].seen = true;
        cycle.users[user].currentWeight = locals.weight;
        cycle.users[user].minimumWeight = locals.weight;
        cycle.users[user].multiplier = locals.multiplier;
        cycle.users[user].redeemable = ptr.users[user].redeemable;
        cycle.users[user].start = ptr.users[user].start;
        locals.lastDaySeen = ptr.users[user].daysClaimed;
        locals.redeemable = 0;
        locals.totalWeight = ptr.totalWeight;

        if (locals.totalWeight > 0 && ptr.reserved > 0) {
          locals.daysToRedeem = 0;
          if (ptr.day - 1 > locals.lastDaySeen)
            locals.daysToRedeem = uint256(ptr.day - 1).sub(locals.lastDaySeen);
          locals.redeemable = locals.daysToRedeem.mul(inflateBy);
          locals.redeemable = locals
            .redeemable
            .mul(locals.weight)
            .mul(locals.multiplier)
            .div(locals.totalWeight)
            .div(1 ether);
          if (locals.multiplier > 1 ether) {
            locals.bonus = uint256(locals.multiplier.sub(1 ether))
              .mul(locals.redeemable)
              .div(locals.multiplier);
            bonusesAccrued[user] = bonusesAccrued[user].add(locals.bonus);
          }
          cycle.users[user].redeemable = cycle.users[user].redeemable.add(
            locals.redeemable
          );
        }

        for (
          locals.i = locals.cycleNumber + 1;
          locals.i < currentCycle;
          locals.i++
        ) {
          ptr = cycles[locals.i];
          locals.totalWeight = ptr.totalWeight;
          ptr.users[user].minimumWeight = locals.weight;
          ptr.users[user].multiplier = locals.multiplier;
          if (locals.totalWeight > 0 && ptr.reserved > 0) {
            locals.redeemable = ptr
              .reserved
              .mul(locals.weight)
              .mul(locals.multiplier)
              .div(ptr.totalWeight)
              .div(1 ether);
            cycle.users[user].redeemable = cycle.users[user].redeemable.add(
              locals.redeemable
            );
          }
        }

        return;
      }
    }
    cycle.users[user].seen = true;
    cycle.users[user].multiplier = 1 ether;
  }

  struct CalculateRewardsLocals {
    uint256 weight;
    uint256 totalWeight;
    uint256 daysToRedeem;
    uint256 amountRedeemed;
  }

  function calculateRewards(
    address _user,
    uint256 amt,
    mapping(uint256 => StakingControllerLib.Cycle) storage cycles,
    mapping(address => uint256) storage bonusesAccrued,
    uint256 inflateBy,
    uint256 currentCycle
  ) external returns (uint256 amountToRedeem, uint256 bonuses) {
    StakingControllerLib.Cycle storage cycle = cycles[currentCycle];
    StakingControllerLib.User storage user = cycle.users[_user];
    CalculateRewardsLocals memory locals;

    locals.weight = Math
      .min(user.minimumWeight, user.currentWeight)
      .mul(user.multiplier)
      .div(1 ether);
    locals.totalWeight = cycle.totalWeight;
    if (locals.totalWeight == 0) locals.totalWeight = 1;
    require(locals.weight > 0 || user.redeemable > 0, 'nothing to redeem');
    if (cycle.day - 1 > user.daysClaimed)
      locals.daysToRedeem = uint256(cycle.day - 1).sub(user.daysClaimed);
    amountToRedeem = inflateBy.mul(locals.daysToRedeem);
    amountToRedeem = amountToRedeem.mul(locals.weight).div(locals.totalWeight);
    if (user.multiplier > 1 ether) {
      bonuses = uint256(user.multiplier.sub(1 ether)).mul(amountToRedeem).div(
        user.multiplier
      );
      bonusesAccrued[_user] = bonusesAccrued[_user].add(bonuses);
    }
    amountToRedeem = amountToRedeem.add(user.redeemable);
    if (amt > 0) locals.amountRedeemed = Math.min(amountToRedeem, amt);
    else locals.amountRedeemed = amountToRedeem;
    require(amountToRedeem > 0, 'nothing left to redeem');
    user.daysClaimed = cycle.day - 1;

    if (locals.amountRedeemed >= amountToRedeem) user.redeemable = 0;
    else user.redeemable = amountToRedeem.sub(locals.amountRedeemed);
    amountToRedeem = locals.amountRedeemed;

    bonuses = bonusesAccrued[_user];
  }

  function determineDailyMultiplier(
    address sender,
    StakingControllerLib.DailyUser storage user,
    StakingControllerLib.UserWeightChanges storage weightChange,
    mapping(uint256 => StakingControllerLib.Tier) storage tiers,
    mapping(address => uint256) storage dailyBonusesAccrued,
    uint256 tiersLength
  ) external returns (uint256 multiplier) {
    StakingControllerLib.DetermineMultiplierLocals memory locals;
    locals.tierIndex = Math.max(user.commitment, user.currentTier);
    locals.tier = tiers[locals.tierIndex];
    locals.multiplier = locals.tierIndex == 0
      ? 1 ether
      : locals.tier.multiplier;
    multiplier = locals.multiplier;

    for (uint256 i = weightChange.totalCyclesSeen; i > 0; i--) {
      locals.minimum = weightChange.changes[i];

      if (locals.minimum < locals.tier.minimum) {
        if (locals.tierIndex > 0) user.currentTier = locals.tierIndex - 1;
        return locals.multiplier;
      }
      locals.cyclesHeld++;
      if (locals.cyclesHeld > locals.tier.cycles) {
        if (user.commitment == locals.tierIndex) {
          user.commitment = 0;
          dailyBonusesAccrued[sender] = 0;
        }
        locals.tierIndex++;
        if (locals.tierIndex == tiersLength)
          return tiers[locals.tierIndex].multiplier;
        locals.tier = tiers[locals.tierIndex];

        locals.multiplier = locals.tier.multiplier;
      }
    }
  }

  function updateRedeemable(
    address sender,
    uint256 inflateBy,
    uint256 currentDay,
    StakingControllerLib.DailyUser storage user,
    uint256[] storage inflateByChanged,
    mapping(uint256 => StakingControllerLib.InflateByChanged)
      storage inflateByValues,
    uint256 totalWeight
  ) external returns (uint256 redeemable, uint256 bonuses) {
    StakingControllerLib.DetermineInflateLocals memory locals;
    locals.lastDayInEpoch = currentDay - 1;
    locals.lastDayChanged = user.lastDaySeen;
    if (inflateByChanged.length > 0) {
      uint256 lastDayChangedIndex = inflateByChanged.length - 1;
      locals.lastDayChanged = inflateByChanged[lastDayChangedIndex];
      for (uint256 i = lastDayChangedIndex; i >= 0; i--) {
        locals.dayChanged = inflateByChanged[i];
        if (user.lastDaySeen >= locals.dayChanged) {
          locals.lastDayChanged = user.lastDaySeen;
          break;
        }
        locals.dayDifference = 0;
        if (locals.dayChanged < currentDay)
          locals.dayDifference = currentDay.sub(locals.dayChanged);
        StakingControllerLib.InflateByChanged memory changed =
          inflateByValues[locals.dayChanged];
        locals.totalWeight = Math.max(changed.totalWeight, totalWeight);
        locals.totalWeight = Math.max(locals.totalWeight, user.lastTotalWeight);
        if (user.commitment > 0) {
          locals.tempBonus = locals
            .dayDifference
            .mul(changed.previousAmount)
            .mul(user.weight)
            .div(locals.totalWeight);
        }
        locals.tempRedeemable = locals.tempBonus.mul(user.multiplier).div(
          1 ether
        );
        locals.redeemable = locals.redeemable.add(locals.tempRedeemable);
        if (user.multiplier > 1 ether)
          locals.tempBonus = locals
            .tempBonus
            .mul(user.multiplier.sub(1 ether))
            .div(1 ether);
        bonuses = bonuses.add(locals.tempBonus);
      }
    }
    if (locals.lastDayChanged < currentDay) {
      locals.dayDifference = currentDay.sub(locals.lastDayChanged);
      locals.totalWeight = Math.max(totalWeight, user.lastTotalWeight);
      if (locals.totalWeight > 0)
        locals.tempRedeemable = locals
          .dayDifference
          .mul(inflateBy)
          .mul(user.weight)
          .div(locals.totalWeight);
      locals.redeemable = locals.redeemable.add(
        locals.tempRedeemable.mul(user.multiplier).div(1 ether)
      );
      if (user.multiplier > 1 ether) {
        bonuses = bonuses.add(
          locals.tempRedeemable.mul(user.multiplier.sub(1 ether)).div(1 ether)
        );
      }
    }
    redeemable = locals.redeemable;
  }

  function determineMultiplier(
    mapping(uint256 => StakingControllerLib.Cycle) storage cycles,
    uint256 currentCycle,
    mapping(uint256 => StakingControllerLib.Tier) storage tiers,
    uint256 tiersLength,
    mapping(address => uint256) storage bonusesAccrued,
    mapping(address => uint256) storage lockCommitments,
    bool penaltyChange,
    address user,
    uint256 currentBalance
  ) external returns (uint256 multiplier, uint256 amountToBurn) {
    StakingControllerLib.DetermineMultiplierLocals memory locals;
    StakingControllerLib.User storage currentUser =
      cycles[currentCycle].users[user];
    locals.minimum = uint256(~0);
    locals.tierIndex = lockCommitments[user];
    locals.tier = tiers[locals.tierIndex];
    locals.cyclesHeld = 0;
    locals.multiplier = locals.tierIndex == 0
      ? 1 ether
      : locals.tier.multiplier;
    for (uint256 i = currentCycle; i > 0; i--) {
      StakingControllerLib.Cycle storage cycle = cycles[i];
      StakingControllerLib.User storage _user = cycle.users[user];
      locals.minimum = Math.min(locals.minimum, _user.minimumWeight);
      currentUser.cyclesHeld = locals.cyclesHeld;
      currentUser.currentTier = locals.tierIndex;
      if (locals.minimum < locals.tier.minimum) {
        if (lockCommitments[user] == locals.tierIndex && penaltyChange) {
          uint256 bonus = bonusesAccrued[user];
          amountToBurn = Math.min(bonus, currentBalance);

          if (amountToBurn > 0) {
            bonusesAccrued[user] = 0;
            lockCommitments[user] = 0;
            currentUser.currentTier = 0;
            currentUser.cyclesHeld = 0;
          }
        }
        return (locals.multiplier, amountToBurn);
      }
      locals.cyclesHeld++;
      if (locals.tierIndex == 0) {
        locals.tierIndex++;
        if (locals.tierIndex > tiersLength)
          return (locals.multiplier, amountToBurn);
        locals.tier = tiers[locals.tierIndex];
      }
      if (locals.cyclesHeld == locals.tier.cycles) {
        locals.multiplier = locals.tier.multiplier;
        locals.tierIndex++;

        lockCommitments[user] = 0;
        bonusesAccrued[user] = 0;
        if (locals.tierIndex > tiersLength)
          return (locals.multiplier, amountToBurn);
        locals.tier = tiers[locals.tierIndex];
      }
    }
    return (locals.multiplier, amountToBurn);
  }

  function updateWeightsWithMultiplier(
    address user,
    uint256 currentCycle,
    uint256 multiplier,
    mapping(uint256 => StakingControllerLib.Cycle) storage cycles
  ) external returns (uint256) {
    StakingControllerLib.Cycle storage cycle = cycles[currentCycle];
    StakingControllerLib.User storage _sender = cycle.users[user];
    StakingControllerLib.UpdateLocals memory locals;
    locals.multiplier = multiplier;
    locals.weight = Math.min(_sender.minimumWeight, _sender.currentWeight);
    locals.prevMul = _sender.multiplier;
    locals.prevRes = locals.weight.mul(locals.prevMul).div(1 ether);
    locals.prevRawRes = _sender.currentWeight.mul(locals.prevMul).div(1 ether);
    locals.nextRes = locals.weight.mul(locals.multiplier).div(1 ether);
    locals.nextRawRes = _sender.currentWeight.mul(locals.multiplier).div(
      1 ether
    );
    if (locals.multiplier != _sender.multiplier) {
      _sender.multiplier = locals.multiplier;
      if (cycle.totalWeight == locals.prevRes)
        cycle.totalWeight = locals.nextRes;
      else
        cycle.totalWeight = cycle.totalWeight.sub(locals.prevRes).add(
          locals.nextRes
        );
      if (cycle.totalRawWeight == locals.prevRawRes)
        cycle.totalRawWeight = locals.nextRawRes;
      else
        cycle.totalRawWeight = cycle.totalRawWeight.sub(locals.prevRawRes).add(
          locals.nextRawRes
        );
    }
    return locals.multiplier;
  }
}

