// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import {StakingController} from "../staking/StakingController.sol";
import {StakingControllerLib} from "../staking/StakingControllerLib.sol";
import {Viewer} from "./Viewer.sol";
import {BytesManip} from "../util/Bytes.sol";
import {console} from "hardhat/console.sol";

contract Query {
    using BytesManip for *;

    constructor(address sc, address user) public {
        address viewLayer = address(new Viewer());

        bytes memory result =
            StakingController(sc).query(
                viewLayer,
                abi.encodeWithSelector(Viewer.render.selector, user)
            );
        (, bytes memory balances) =
            sc.call(abi.encodeWithSignature("_claim(address)", user));

        bytes memory res =
            abi.decode(balances.slice(4, balances.length - 4), (bytes));
        if (res.length != 64) {
            result = result.concat(abi.encode(uint256(0), uint256(0)));
        } else {
            result = result.concat(res);
        }

        assembly {
            return(add(0x20, result), mload(result))
        }
    }

    function decode()
        public
        returns (
            StakingControllerLib.User memory returnedUser,
            StakingControllerLib.Tier memory tier,
            StakingControllerLib.EncodeableCycle memory cycle,
            StakingControllerLib.ReturnStats memory returnstats,
            StakingControllerLib.DailyUser memory user,
            uint256 redeemable,
            uint256 bonuses,
            StakingControllerLib.Tier[] memory tiers
        )
    {}
}

