// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import {
  StakingControllerTemplate
} from '../staking/StakingControllerTemplate.sol';
import { StakingControllerLib } from '../staking/StakingControllerLib.sol';
import {
  MathUpgradeable as Math
} from '@openzeppelin/contracts-upgradeable/math/MathUpgradeable.sol';
import {
  SafeMathUpgradeable
} from '@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol';

contract Viewer is StakingControllerTemplate {
  using SafeMathUpgradeable for *;

  function render(address caller)
    public
    view
    returns (
      StakingControllerLib.User memory returnedUser,
      StakingControllerLib.Tier memory tier,
      StakingControllerLib.EncodeableCycle memory retCycle,
      StakingControllerLib.ReturnStats memory returnstats,
      StakingControllerLib.DailyUser memory dailyUser,
      StakingControllerLib.Tier[] memory _tiers
    )
  {
    uint256 commitment = lockCommitments[caller];
    _tiers = new StakingControllerLib.Tier[](tiersLength);
    for (uint256 i = 0; i < tiersLength; i++) {
      _tiers[i] = tiers[i];
    }
    tier = tiers[commitment];
    StakingControllerLib.Cycle storage _cycle = cycles[currentCycle];

    StakingControllerLib.User memory _user = _cycle.users[caller];

    dailyUser = dailyUsers[caller];
    {
      returnstats.staked = sCnfi.balanceOf(caller);
    }
    uint256 lastSeenCycle = currentCycle;
    StakingControllerLib.DetermineMultiplierLocals memory locals;
    returnstats.cycleChange = dailyUser.cyclesHeld;
    (dailyUser.cycleEnd, dailyUser.cyclesHeld) = computeCyclesHeld(
      dailyUser.cycleEnd,
      cycleInterval,
      dailyUser.cyclesHeld,
      block.timestamp
    );
    StakingControllerLib.UserWeightChanges storage _weightChange =
      weightChanges[caller];
    uint256[] memory changes = new uint256[](dailyUser.cyclesHeld);
    if (dailyUser.cyclesHeld > 0) {
      for (uint256 i = 0; i < dailyUser.cyclesHeld - 1; i++) {
        if (i < returnstats.cycleChange) {
          changes[i] = _weightChange.changes[i];
        } else {
          changes[i] = dailyUser.weight;
        }
      }
    }

    locals.tierIndex = Math.max(dailyUser.commitment, dailyUser.currentTier);
    locals.tier = tiers[locals.tierIndex];
    if (dailyUser.cyclesHeld > 0) {
      for (uint256 i = changes.length - 1; i > 0; i--) {
        locals.minimum = changes[i];
        if (locals.minimum < locals.tier.minimum) {
          break;
        }
        locals.cyclesHeld++;
        if (locals.cyclesHeld > locals.tier.cycles) {
          locals.tierIndex++;
          if (locals.tierIndex == tiersLength) {
            locals.tier = tiers[locals.tierIndex];
            break;
          }
          locals.tier = tiers[locals.tierIndex];
          locals.multiplier = locals.tier.multiplier;
        }
      }
    }
    {
      StakingControllerLib.Tier memory temptier;
      for (uint256 i = 0; i < tiersLength; i++) {
        temptier = tiers[i];
        if (returnstats.staked < temptier.minimum) {
          _user.currentTier = i - 1;
          break;
        }
      }
      if (returnstats.staked == 0) {
        locals.tier = tiers[0];
      }
    }
    _cycle = cycles[currentCycle];
    retCycle = StakingControllerLib.EncodeableCycle(
      _cycle.totalWeight,
      _cycle.totalRawWeight,
      _cycle.pCnfiToken,
      _cycle.reserved,
      _cycle.day,
      _cycle.canUnstake,
      lastSeenCycle,
      currentCycle
    );

    {
      returnstats.totalStakedInProtocol = sCnfi.totalSupply();
      returnstats.currentCnfiBalance = cnfi.balanceOf(caller);
    }
    returnstats.cnfiReleasedPerDay = inflateBy;
    returnstats.basePenalty = baseUnstakePenalty;
    returnstats.commitmentViolationPenalty = commitmentViolationPenalty;

    returnstats.totalWeight = totalWeight;
    returnstats.currentDay = currentDay;
    return (_user, locals.tier, retCycle, returnstats, dailyUser, _tiers);
  }
}

